import React, { useEffect, useState } from "react";
import { 
  UserCircleIcon, 
  DocumentChartBarIcon, 
  PrescriptionIcon, 
  UsersIcon 
} from "@heroicons/react/24/outline";

const Home = () => {
  const [stats, setStats] = useState({
    medicalRecords: 0,
    prescriptions: 0,
    patients: 0,
  });

  const [userInfo, setUserInfo] = useState({
    name: "",
    role: "",
    email: ""
  });

  useEffect(() => {
    // Gọi API lấy thống kê
    fetch("https://api.example.com/clinic-stats")
      .then((response) => response.json())
      .then((data) => {
        setStats({
          medicalRecords: data.medicalRecords || 0,
          prescriptions: data.prescriptions || 0,
          patients: data.patients || 0,
        });
      })
      .catch((error) => console.error("Error fetching stats:", error));

    // Gọi API lấy thông tin người dùng
    fetch("http://localhost:5001/api/users/users")
    .then((res) => res.json())
    .then(() => {
      setUserInfo({
        "id": "abc123",
        "name": "Phạm Quốc Toàn",
        "address": "Tuy Phước",
        "email": "toan@example.com",
        "role": "Nhân viên",
      });
    })
    .catch((error) => console.error("Error fetching user info:", error));
  }, []);

  const StatCard = ({ title, value, Icon, bgColor }) => (
    <div className="bg-white shadow-lg rounded-xl p-5 hover:shadow-xl transition-all duration-300">
      <div className="flex justify-between items-center">
        <div>
          <h3 className="text-sm font-medium text-gray-500 uppercase tracking-wider mb-2">
            {title}
          </h3>
          <p className="text-2xl font-bold text-gray-900">{value}</p>
        </div>
        <div className={`p-3 rounded-full ${bgColor} bg-opacity-20`}>
          <Icon className={`h-8 w-8 ${bgColor}`} />
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-6xl mx-auto">
        {/* User Profile Card */}
        <div className="bg-white shadow-md rounded-xl p-6 mb-6">
          <div className="flex items-center space-x-4">
            <UserCircleIcon className="h-16 w-16 text-blue-500" />
            <div>
              <h2 className="text-2xl font-bold text-gray-800">Xin chào, {userInfo.name}</h2>
              <div className="text-gray-600 space-y-1">
                <p>ID: {userInfo.id}</p>
                <p>Email: {userInfo.email}</p>
                <p>Vai trò: {userInfo.role}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Statistics Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <StatCard 
            title="Giấy khám bệnh" 
            value={stats.medicalRecords} 
            Icon={DocumentChartBarIcon} 
            bgColor="text-red-500"
          />
          <StatCard 
            title="Đơn Thuốc" 
            value={stats.prescriptions} 
            Icon={PrescriptionIcon} 
            bgColor="text-green-500"
          />
          <StatCard 
            title="Bệnh nhân" 
            value={stats.patients} 
            Icon={UsersIcon} 
            bgColor="text-blue-500"
          />
        </div>
      </div>
    </div>
  );
};

export default Home;
