import { useParams, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const EditPatient = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5001/api/patients/${id}`)
      .then((res) => res.json())
      .then((data) => setPatient(data))
      .catch(() => alert("Lỗi tải dữ liệu!"));
  }, [id]);

  const handleSave = () => {
    alert("Lưu thành công!");
    navigate("/patients"); // Quay lại danh sách bệnh nhân sau khi lưu
  };

  if (!patient) return <p>Loading...</p>;

  return (
    <div>
      <h2>Chỉnh sửa bệnh nhân: {patient.name}</h2>
      <form>
        <input type="text" value={patient.name} onChange={(e) => setPatient({ ...patient, name: e.target.value })} />
        <button type="button" onClick={handleSave}>Lưu</button>
      </form>
    </div>
  );
};

export default EditPatient;
