import React, { useState, useEffect } from "react";
import {
    Card,
    CardBody,
    Container,
    Row,
    Col,
    Input,
    Button,
    FormGroup,
    Label,
} from "reactstrap";
import { useParams, useNavigate, useLocation } from "react-router-dom";

const EditMedicineType = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const location = useLocation();
    const [medicineTypes, setMedicineTypes] = useState(location.state?.medicineTypes || []);
    const [setMedicineTypesFunc] = useState(location.state?.setMedicineTypes);
    const [medicineType, setMedicineType] = useState({
        code: "",
        name: "",
    });

    useEffect(() => {
        if (medicineTypes.length > 0) {
            const type = medicineTypes.find((type) => type.id === parseInt(id));
            if (type) {
                setMedicineType(type);
            }
        }
    }, [id, medicineTypes]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setMedicineType({ ...medicineType, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (setMedicineTypesFunc) {
            setMedicineTypesFunc(
                medicineTypes.map((type) =>
                    type.id === parseInt(id) ? medicineType : type
                )
            );
        }
        navigate("/admin/medicine-types");
    };

    if (medicineTypes.length === 0) {
        return (
            <Container className="mt-4" fluid>
                <Row className="mb-4">
                    <Col>
                        <h3 className="text-dark">Loại thuốc không tồn tại</h3>
                    </Col>
                </Row>
            </Container>
        );
    }

    return (
        <>
            <Container className="mt-4" fluid>
                <Row className="mb-4">
                    <Col md="6">
                        <h3 className="text-dark">Chỉnh Sửa Loại Thuốc</h3>
                    </Col>
                </Row>

                <Card>
                    <CardBody>
                        <form onSubmit={handleSubmit}>
                            <FormGroup>
                                <Label for="code">Mã Loại Thuốc</Label>
                                <Input
                                    type="text"
                                    name="code"
                                    id="code"
                                    value={medicineType.code}
                                    onChange={handleInputChange}
                                    required
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="name">Tên Loại Thuốc</Label>
                                <Input
                                    type="text"
                                    name="name"
                                    id="name"
                                    value={medicineType.name}
                                    onChange={handleInputChange}
                                    required
                                />
                            </FormGroup>
                            <Button color="primary" type="submit">
                                Lưu
                            </Button>
                        </form>
                    </CardBody>
                </Card>
            </Container>
        </>
    );
};

export default EditMedicineType;
