import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';

const PatientForm = () => {
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({
    fullName: '',
    gender: 'Nam',
    birthDate: '',
    phone: '',
    address: '',
    image: null
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setFormData(prev => ({
      ...prev,
      image: file
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form data:', formData);
    // Thêm logic gửi dữ liệu ở đây nếu cần
  };

  const handleReset = () => {
    setFormData({
      fullName: '',
      gender: 'Nam',
      birthDate: '',
      phone: '',
      address: '',
      image: null
    });
  };

  return (
    <Card className="w-full max-w-4xl">
      <CardHeader>
        <CardTitle className="text-2xl">Thêm bệnh nhân</CardTitle>
        <p className="text-sm text-gray-500">Điền tất cả thông tin bên dưới</p>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Cột bên trái */}
            <div className="space-y-4">
              <div>
                <Label htmlFor="fullName">
                  Họ và tên <span className="text-red-500">*</span>
                </Label>
                <Input
                  id="fullName"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleInputChange}
                  placeholder="Họ và tên"
                  className="mt-1"
                />
              </div>

              <div>
                <Label>
                  Giới tính <span className="text-red-500">*</span>
                </Label>
                <RadioGroup
                  name="gender"
                  value={formData.gender}
                  onValueChange={(value) => handleInputChange({ target: { name: 'gender', value }})}
                  className="flex gap-4 mt-1"
                >
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="Nam" id="nam" />
                    <Label htmlFor="nam">Nam</Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="Nữ" id="nu" />
                    <Label htmlFor="nu">Nữ</Label>
                  </div>
                </RadioGroup>
              </div>

              <div>
                <Label htmlFor="birthDate">
                  Ngày sinh <span className="text-red-500">*</span>
                </Label>
                <Input
                  id="birthDate"
                  name="birthDate"
                  type="date"
                  value={formData.birthDate}
                  onChange={handleInputChange}
                  className="mt-1"
                />
              </div>
            </div>

            {/* Cột bên phải */}
            <div className="space-y-4">
              <div>
                <Label htmlFor="image">
                  Ảnh <span className="text-red-500">*</span>
                </Label>
                <Input
                  id="image"
                  name="image"
                  type="file"
                  onChange={handleImageChange}
                  className="mt-1"
                  accept="image/*"
                />
              </div>

              <div>
                <Label htmlFor="phone">
                  SĐT <span className="text-red-500">*</span>
                </Label>
                <Input
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  placeholder="Số điện thoại"
                  className="mt-1"
                />
              </div>

              <div>
                <Label htmlFor="address">
                  Địa chỉ <span className="text-red-500">*</span>
                </Label>
                <Input
                  id="address"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  placeholder="Địa chỉ"
                  className="mt-1"
                />
              </div>
            </div>
          </div>

          <div className="flex gap-4 mt-6">
            <Button type="submit" className="bg-teal-700 hover:bg-teal-800">
              Lưu lại
            </Button>
            <Button 
              type="button" 
              onClick={() => navigate('/patient')} // Điều hướng về danh sách bệnh nhân
              className="bg-red-500 hover:bg-red-600"
            >
              Quay lại
            </Button>
          </div>
        </form>
      </CardContent>
    </Card>
  );
};

export default PatientForm;
