import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";

const EditPatient = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [patient, setPatient] = useState({
    patient_id: "",
    name: "",
    phone: "",
    gender: "Nam",
    birth_date: "",
    address: "",
  });

  useEffect(() => {
    fetch(`http://localhost:5001/api/patients/${id}`)
      .then((res) => res.json())
      .then((data) => setPatient(data))
      .catch(() => alert("Lỗi tải dữ liệu!"));
  }, [id]);

  const handleChange = (e) => {
    setPatient({ ...patient, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`http://localhost:5001/api/patients/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify(patient),
    })
      .then((res) => res.ok && navigate("/admin/patient"))
      .catch(() => alert("Lỗi cập nhật dữ liệu!"));
  };

  return (
    <div className="container">
      <h2 className="text-xl font-bold mb-4">Chỉnh sửa bệnh nhân</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>ID</label>
          <input name="patient_id" value={patient.patient_id} onChange={handleChange} disabled />
        </div>
        <div>
          <label>Họ và tên</label>
          <input name="name" value={patient.name} onChange={handleChange} />
        </div>
        <div>
          <label>Số điện thoại</label>
          <input name="phone" value={patient.phone} onChange={handleChange} />
        </div>
        <div>
          <label>Giới tính</label>
          <select name="gender" value={patient.gender} onChange={handleChange}>
            <option value="Nam">Nam</option>
            <option value="Nữ">Nữ</option>
          </select>
        </div>
        <div>
          <label>Ngày sinh</label>
          <input type="date" name="birth_date" value={patient.birth_date} onChange={handleChange} />
        </div>
        <div>
          <label>Địa chỉ</label>
          <input name="address" value={patient.address} onChange={handleChange} />
        </div>
        <button type="submit">Lưu thay đổi</button>
        <button type="button" onClick={() => navigate("/admin/patient")}>Quay lại</button>
      </form>
    </div>
  );
};

export default EditPatient;
