import React, { useState, useEffect } from 'react';
import { Stethoscope, Users, Clock } from 'lucide-react';
import 'tailwindcss/tailwind.css';

const ClinicLanding = () => {
  const [stats, setStats] = useState({
    medicalRecords: 0,
    prescriptions: 0,
    patients: 0,
  });

  useEffect(() => {
    fetch("https://api.example.com/clinic-stats")
      .then((response) => response.json())
      .then((data) => {
        setStats({
          medicalRecords: data.medicalRecords || 0,
          prescriptions: data.prescriptions || 0,
          patients: data.patients || 0,
        });
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Unified Header */}
      <header className="w-full bg-blue-700 shadow-lg fixed top-0 left-0 z-50 py-4 px-10">
        <div className="container mx-auto flex items-center justify-between">
          <div className="text-2xl font-bold text-white drop-shadow-md">Clinic</div>
          <nav className="flex space-x-8">
            <a href="#services" className="text-white hover:text-gray-200 transition">Services</a>
            <a href="#about" className="text-white hover:text-gray-200 transition">About Us</a>
            <a href="#doctors" className="text-white hover:text-gray-200 transition">Doctors</a>
            <a href="#contact" className="text-white hover:text-gray-200 transition">Contact</a>
          </nav>
          <div className="flex space-x-4 ml-auto justify-end">
            <a href="/login" className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition font-semibold shadow-md">Login</a>
            <a href="/register" className="bg-green-500 text-white px-6 py-2 rounded-lg hover:bg-green-600 transition font-semibold shadow-md">Register</a>
          </div>
        </div>
      </header>

      {/* Hero Section */}
      <div className="relative h-screen flex flex-col justify-center items-center text-center pt-32 px-4">
        <h1 className="text-5xl md:text-6xl font-bold text-gray-800 drop-shadow-lg">Clinic Management System</h1>
        <p className="text-lg mt-4 mb-6 text-gray-600 max-w-2xl">Your Health, Our Priority – Comprehensive care at your fingertips.</p>
      </div>

      {/* Stats Section */}
      <div className="bg-white py-12 shadow-lg -mt-20 rounded-t-xl relative container mx-auto px-6">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 text-center">
          <div>
            <h2 className="text-4xl font-bold">{stats.medicalRecords}</h2>
            <p className="text-gray-600 text-lg">Medical Records</p>
          </div>
          <div>
            <h2 className="text-4xl font-bold">{stats.prescriptions}</h2>
            <p className="text-gray-600 text-lg">Prescriptions</p>
          </div>
          <div>
            <h2 className="text-4xl font-bold">{stats.patients}</h2>
            <p className="text-gray-600 text-lg">Patients</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ClinicLanding;
